import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import {
  BurgerIngridients,
  IngridientWrapper,
  BurgerDetails,
  DetailsTitle,
  IngridientsDetails,
  Total
} from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";

import { BurgerContext } from "../../context/burgerContext";
import useToppings from "../../hooks/useToppings";
import IngridientdetailBlock from "../../component/IngridientsDetailBlock";

export default function Index() {
  const { burger, setBurger } = useContext(BurgerContext);

  const { error, isLoading, allToppings } = useToppings(
    "http://localhost:3000/toppings",
    burger ? burger.consists_of : []
  );
  const [toppings, setToppings] = useState([]);

  useEffect(() => {
    setBurger(JSON.parse(localStorage.getItem("burger")));
  }, [isLoading]);


  const add = (text, num) => {
    for (let i = 0; i < allToppings.length; i++) {
      if (allToppings[i].name === text) {
        allToppings[i].quantity = num;
      }
    }

    setToppings(allToppings.filter(el => el.quantity > 0));
    console.log(allToppings.filter(el => el.quantity > 0));
  };


  if (!isLoading)
    return (
      <Wrapper>
        <Menu>
          <MenuTitle>{burger && burger.name}</MenuTitle>
          <IngridientWrapper>
            <BurgerIngridients>
              {allToppings &&
                allToppings.map(({ name, quantity, id }) => {
                  return (
                    <Ingridient
                      key={id}
                      text={name}
                      add={add}
                      initValue={quantity}
                    />
                  );
                })}
            </BurgerIngridients>
            <BurgerDetails>
              <DetailsTitle>{burger && burger.name} | інформація</DetailsTitle>
              <IngridientsDetails>
                {allToppings.filter(el => el.quantity > 0).map((el) => {
                  return (
                    <IngridientdetailBlock
                      key={el.id}
                      text={el.name}
                      price={el.quantity * el.cost}
                      qty={el.quantity}
                    ></IngridientdetailBlock>
                  );
                })}
              </IngridientsDetails>
              <Total>Загалом(грн.)</Total>
            </BurgerDetails>
          </IngridientWrapper>
        </Menu>
      </Wrapper>
    );

  if (isLoading) {
    return (
      <Wrapper>
        <Menu>Loading...</Menu>
      </Wrapper>
    );
  }
}
