import React, { useContext, useEffect, useState } from "react";
import { BurgerContext } from "../context/burgerContext";
import { Burgers } from "../pages/home/homestyled";

export default function useToppings(db_url, burger_toppings) {
  const [allToppings, setAllToppings] = useState();
  const [error, setError] = useState({ msg: "", status: false });
  const [isLoading, setIsLoading] = useState(true);
  if (!burger_toppings && JSON.parse(localStorage.getItem("burger")))
    burger_toppings = JSON.parse(localStorage.getItem("burger")).consists_of;



  useEffect(() => {
    setIsLoading(true);
    fetch(db_url)
      .then((res) => res.json())
      .then((res) => {
        setAllToppings(res)
        setIsLoading(false);
      })
      .catch((e) => {
        setIsLoading(true);
        setError({ msg: "Виникла помилка" + e, status: true });
      });
  }, [db_url]);

  return { error, isLoading, allToppings};
}
