import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import { BurgerIngridients, IngridientWrapper, BurgerDetails, DetailsTitle, IngridientsDetails } from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";

import { BurgerContext } from "../../context/burgerContext";
import useToppings from "../../hooks/useToppings";
import IngridientdetailBlock from '../../component/IngridientsDetailBlock'

export default function Index() {
  const { burger, setBurger } = useContext(BurgerContext);
  const { toppings, error, isLoading } = useToppings(
    "http://localhost:3000/toppings",
    burger ? burger.consists_of : []
  );
  const [qtyOneToppings, setToppingsOne] = useState([])

  useEffect(() => {
    let arr = toppings.filter(el => el.quantity === 1)
    setToppingsOne(arr);
  }, [toppings])

  useEffect(() => {
    setBurger(JSON.parse(localStorage.getItem("burger")));
  }, []);

  if (isLoading) {
    return (
      <Wrapper>
        <Menu>Loading...</Menu>
      </Wrapper>
    );
  }

  if (!isLoading)
    return (
      <Wrapper>
        <Menu>
          <MenuTitle>{burger.name}</MenuTitle>
          <IngridientWrapper>
            <BurgerIngridients>
              {toppings &&
                toppings.map(({ name, quantity, id }) => {
                  return (
                    <Ingridient key={id} text={name} initValue={quantity} />
                  );
                })}
            </BurgerIngridients>
            <BurgerDetails>
              <DetailsTitle>{burger.name} | інформація</DetailsTitle>
              <IngridientsDetails>
                {arr.map(el => {
                  return (
                  <IngridientdetailBlock>{el.name}</IngridientdetailBlock>
                  );
                })}
              </IngridientsDetails>
            </BurgerDetails>
          </IngridientWrapper>
        </Menu>
      </Wrapper>
    );
}
