import React, {useEffect, useState } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import {BurgerIngridients} from './burgerredostyled'
import Ingridient from '../../component/Ingridient'
import { useHistory } from "react-router-dom";

export default function Index({ id }) {
  const [cachedBurger, setCachedBurger] = useState("");
  const [loading, setLoading] = useState(true)
  const history  = useHistory()

  useEffect(() => {
    setLoading(true)
    if (JSON.parse(localStorage.getItem("burger"))) {
      console.log('yes');
      setCachedBurger(JSON.parse(localStorage.getItem("burger")));
      setLoading(false)
    }
  }, []);
  if(loading) {
    setTimeout(() => {
      history.push('/')
    }, 2000)
    return (
      <span>Loading... | redirecting in 2 seconds</span>
    );
  }
  if(!loading)
  return (
    <Wrapper>
      <Menu>
        <MenuTitle>{cachedBurger.name}</MenuTitle>
        <BurgerIngridients>
            {cachedBurger.consists_of.map(({name, quantity}) => {
                return (
                    <Ingridient key={id} text={name} initValue={quantity} increase={() => {}} descrease={() => {}}/>
                );
            })}
        </BurgerIngridients>
      </Menu>
    </Wrapper>
  );
}
