import React, {useState, useEffect, useContext} from "react";

import {
  IngridientWrapper,
  IngridientButton,
  IngridientText,
  IngridientCount,
  JustWrapper
} from "./ingridientstyled";

import { BurgerContext } from "../../context/burgerContext";


export default function Index({ text, initValue, increase, descrease, ...props }) {
    const [number, setNumber] = useState(initValue)
    const { burger, setBurger } = useContext(BurgerContext);


    useEffect(() => {
      setBurger(p => ({consists_of: burger.consists_of.map(el => el.name.toUpperCase() === text.toUpperCase() ? console.log(el,number) : el.quantity), ...p}))
      // console.log(burger, number)
    }, [number])

  return (
    <IngridientWrapper>
        <IngridientText>{text}</IngridientText>
      <JustWrapper>
        <IngridientButton bg={"red"} onClick={() => setNumber(p => p > 0 ? p - 1 : 0)}>-</IngridientButton>
        <IngridientCount>{number}</IngridientCount>
        <IngridientButton bg={"lightgreen"} onClick={() => setNumber(p => p + 1)}>+</IngridientButton>
      </JustWrapper>
    </IngridientWrapper>
  );
}
