import produce from "immer";

const initialState = {
  id: null,
  name: "",
  cost: 0,
  consists_of: []
};

export default function (state = initialState, action) {
  console.log(action);
  switch (action.type) {
    case "ADD_BURGER": {
      return produce(state, (draftState) => {
        draftState.burgers.push(action.payload);
      });
    }
    case "REMOVE_BURGER": {
      return produce(state, (draftState) => {
        draftState.burgers.filter((el) => el.id !== action.payload);
      });
    }
    default:
      return state;
  }
}
