import React from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle, Burgers } from "../home/homestyled";
import Burger from "../../component/burger";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";

export default function Index() {
  const list = useSelector((state) => state.burgerBucket);
  const dispatch = useDispatch()

  useEffect(() => {
    console.log(list)
    if (list) {
      localStorage.setItem("burgersBucker", JSON.stringify(list.burgers));
    } else {
      let localStorageBurger = JSON.parse(localStorage.getItem("burgersBucker"));
      dispatch({ type: "ADD_BURGER_BUCKET", payload: localStorageBurger });
    }
  }, []);

  return (
    <Wrapper>
      <Menu>
        <MenuTitle>My Bucket</MenuTitle>
        <Burgers>
          {list.burgers.map((burger) => {
            return <Burger key={burger.id} text={burger.name} cost={burger.cost}/>;
          })}
        </Burgers>
      </Menu>
    </Wrapper>
  );
}
