import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import {
  BurgerIngridients,
  IngridientWrapper,
  BurgerDetails,
  DetailsTitle,
  IngridientsDetails,
  Total,
  DetailsButtonContinue,
} from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";

import { BurgerContext } from "../../context/burgerContext";
import useToppings from "../../hooks/useToppings";
import IngridientdetailBlock from "../../component/IngridientsDetailBlock";
import { useHistory } from "react-router-dom";

export default function Index() {
  const { burger, setBurger } = useContext(BurgerContext);
  const history = useHistory();

  const { error, isLoading, allToppings } = useToppings(
    "http://localhost:3000/toppings",
    burger ? burger.consists_of : []
  );
  const [toppings, setToppings] = useState([]);



  const add = (text, num) => {
    for (let i = 0; i < allToppings.length; i++) {
      if (allToppings[i].name === text) {
        allToppings[i].quantity = num;
      }
    }

    console.log(allToppings.filter((el) => el.quantity > 0));
  };

  const sumOfToppings = () => {
    let sum = 0;
    for (let i = 0; i < allToppings.length; i++) {
      sum += allToppings[i].cost * allToppings[i].quantity;
    }
    return sum;
  };

  if (!isLoading) {
    return (
      <Wrapper>
        <Menu>
          <MenuTitle>{burger && burger.name}</MenuTitle>
          <IngridientWrapper>
            <BurgerIngridients>
              {allToppings &&
                allToppings.map(({ name, quantity, id }) => {
                  return (
                    <Ingridient
                      key={id}
                      text={name}
                      add={add}
                      initValue={quantity}
                    />
                  );
                })}
            </BurgerIngridients>
            <BurgerDetails>
              <DetailsTitle>
                <span>{burger && burger.name} | інформація</span>{" "}
                <DetailsButtonContinue
                  onClick={() => {
                    localStorage.setItem("burger", JSON.stringify({...burger, consists_of: allToppings.filter((el) => el.quantity > 0)}))
                    history.push("/bucket");
                  }}
                >
                  продовжити
                </DetailsButtonContinue>
              </DetailsTitle>
              <IngridientsDetails>
                {allToppings
                  .filter((el) => el.quantity > 0)
                  .map((el) => {
                    return (
                      <IngridientdetailBlock
                        key={el.id}
                        text={el.name}
                        price={el.quantity * el.cost}
                        qty={el.quantity}
                      ></IngridientdetailBlock>
                    );
                  })}
              </IngridientsDetails>
              <Total>Загалом {sumOfToppings()}(грн.)</Total>
            </BurgerDetails>
          </IngridientWrapper>
        </Menu>
      </Wrapper>
    );
  }

  if (isLoading) {
    return (
      <Wrapper>
        <Menu>Loading...</Menu>
      </Wrapper>
    );
  }
}
