import produce from "immer";

const initialState = {
  isLoading: true,
  error: "",
  burgers: [],
};

export default function (state = initialState, action) {
  console.log(action);
  switch (action.type) {
    case "ADD_BURGER": {
      produce((state, draftState) => {
        draftState.burgers.push(action.payload);
      });
      return;
    }
    case "REMOVE_BURGER": {
      produce((state, draftState) => {
        draftState.burgers.filter((el) => el.id !== action.payload);
      });
      return;
    }
    default:
      return state;
  }
}
