import React, { useContext, useEffect, useState } from "react";
import { BurgerContext } from "../../context/burgerContext";

export default function Index({ id }) {
  const { burger, setBurger } = useContext(BurgerContext);
  const [cachedBurger, setCachedBurger] = useState('виберіть бургер');

  useEffect(() => {
    if (burger.name && JSON.parse(localStorage.getItem("burger"))) {
      setCachedBurger(JSON.parse(localStorage.getItem("burger")));
    }
  }, []);
  if (!burger.name) {
    return <div>{cachedBurger.name}</div>;
  }
  return <div>{burger.name}</div>;
}
