import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import {
  BurgerIngridients,
  IngridientWrapper,
  BurgerDetails,
  DetailsTitle,
  IngridientsDetails,
  Total,
  DetailsButtonContinue,
} from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";

import { BurgerContext } from "../../context/burgerContext";
import useToppings from "../../hooks/useToppings";
import IngridientdetailBlock from "../../component/IngridientsDetailBlock";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

export default function Index() {
  const burger = useSelector((state) => state.burger);
  const dispatch = useDispatch();
  const history = useHistory();

  const { toppings, error, isLoading, allToppings } = useToppings(
    "http://localhost:3000/toppings",
    burger.consists_of
  );

  useEffect(() => {
    if (burger.id) {
      localStorage.setItem("burger", JSON.stringify(burger));
    } else {
      let localStorageBurger = JSON.parse(localStorage.getItem("burger"));
      dispatch({ type: "ADD_BURGER", payload: localStorageBurger });
    }
  }, []);

  const add = (text, num) => {
    console.log(num)
  };

  const sumOfToppings = () => {
    let sum = 0;
    for (let i = 0; i < allToppings.length; i++) {
      sum += allToppings[i].cost * allToppings[i].quantity;
    }
    return sum;
  };

  return (
    <Wrapper>
      <Menu>
        <MenuTitle>{burger && burger.name}</MenuTitle>
        <IngridientWrapper>
          <BurgerIngridients>
            {toppings.map(({ name, quantity, id }) => {
              return (
                <Ingridient
                  key={id}
                  text={name}
                  add={add}
                  initValue={quantity}
                />
              );
            })}
          </BurgerIngridients>
          <BurgerDetails>
            <DetailsTitle>
              <span>{burger && burger.name} | інформація</span>{" "}
              <DetailsButtonContinue
                onClick={() => {
                  localStorage.setItem(
                    "burger",
                    JSON.stringify({
                      ...burger,
                      consists_of: allToppings.filter((el) => el.quantity > 0),
                    })
                  );
                  history.push("/bucket");
                }}
              >
                продовжити
              </DetailsButtonContinue>
            </DetailsTitle>
            <IngridientsDetails>
              {toppings
                .filter((el) => el.quantity > 0)
                .map((el) => {
                  return (
                    <IngridientdetailBlock
                      key={el.id}
                      text={el.name}
                      price={el.quantity * el.cost}
                      qty={el.quantity}
                    ></IngridientdetailBlock>
                  );
                })}
            </IngridientsDetails>
            <Total>Загалом {sumOfToppings()}(грн.)</Total>
          </BurgerDetails>
        </IngridientWrapper>
      </Menu>
    </Wrapper>
  );
}
