import React, {useState, useEffect} from "react";

import {
  IngridientWrapper,
  IngridientButton,
  IngridientText,
  IngridientCount,
  JustWrapper
} from "./ingridientstyled";

export default function Index({ text, initValue, increase, descrease, ...props }) {
    const [number, setNumber] = useState(initValue)
  return (
    <IngridientWrapper>
        <IngridientText>{text}</IngridientText>
      <JustWrapper>
        <IngridientButton bg={"red"} onClick={() => setNumber(p => p > 0 ? p - 1 : 0)}>-</IngridientButton>
        <IngridientCount>{number}</IngridientCount>
        <IngridientButton bg={"lightgreen"} onClick={() => setNumber(p => p + 1)}>+</IngridientButton>
      </JustWrapper>
    </IngridientWrapper>
  );
}
