import React, {useState, useEffect, useContext} from "react";

import {
  IngridientWrapper,
  IngridientButton,
  IngridientText,
  IngridientCount,
  JustWrapper
} from "./ingridientstyled";

import { BurgerContext } from "../../context/burgerContext";


export default function Index({ text, initValue,setUpdBurger, ...props }) {
    const [number, setNumber] = useState(initValue)
    const { burger, setBurger } = useContext(BurgerContext);


    useEffect(() => {

      for(let i = 0; i < burger.consists_of.length; i++) {
        if(burger.consists_of[i].name === text) {
          burger.consists_of[i].quantity = number;
        }
      }
      setBurger(burger)
      localStorage.setItem('burger',JSON.stringify(burger))
      setUpdBurger(burger)
    }, [number])

  return (
    <IngridientWrapper>
        <IngridientText>{text}</IngridientText>
      <JustWrapper>
        <IngridientButton bg={"red"} onClick={() => setNumber(p => p > 0 ? p - 1 : 0)}>-</IngridientButton>
        <IngridientCount>{number}</IngridientCount>
        <IngridientButton bg={"lightgreen"} onClick={() => setNumber(p => p + 1)}>+</IngridientButton>
      </JustWrapper>
    </IngridientWrapper>
  );
}
