import React, { useState, useEffect, useContext } from "react";

import {
  IngridientWrapper,
  IngridientButton,
  IngridientText,
  IngridientCount,
  JustWrapper,
} from "./ingridientstyled";

import { BurgerContext } from "../../context/burgerContext";

export default function Index({ text, initValue, add, ...props }) {
  const [number, setNumber] = useState(initValue);
  const { burger, setBurger } = useContext(BurgerContext);

  return (
    <IngridientWrapper>
      <IngridientText>{text}</IngridientText>
      <JustWrapper>
        <IngridientButton
          bg={"red"}
          onClick={() => {
            setNumber((p) => (p > 0 ? p - 1 : 0));
            add(text,number);
          }}
        >
          -
        </IngridientButton>
        <IngridientCount>{number}</IngridientCount>
        <IngridientButton
          bg={"lightgreen"}
          onClick={() => {
            setNumber((p) => p + 1);
            add(text,number);
          }}
        >
          +
        </IngridientButton>
      </JustWrapper>
    </IngridientWrapper>
  );
}
