import React, {useState, useEffect} from "react";

import {
  IngridientWrapper,
  IngridientButton,
  IngridientText,
  IngridientCount,
  JustWrapper
} from "./ingridientstyled";

export default function Index({ text, increase, descrease, ...props }) {
    const [number, setAdd] = useState(1)
    useEffect(() => {
        if(number <=0) setAdd(0)
    }, [number])
  return (
    <IngridientWrapper>
        <IngridientText>{text}</IngridientText>
      <JustWrapper>
        <IngridientButton bg={"red"} onClick={() => setAdd(p => p - 1)}> - </IngridientButton>
        <IngridientCount>{number}</IngridientCount>
        <IngridientButton bg={"lightgreen"} onClick={() => setAdd(p => p + 1)}> + </IngridientButton>
      </JustWrapper>
    </IngridientWrapper>
  );
}
