import React, { useEffect, useState } from "react";
import { Burgers } from "../pages/home/homestyled";

export default function useToppings(db_url, burger_toppings) {
  const [toppings, setToppings] = useState([]);
  const [error, setError] = useState({ msg: "", status: false });
  const [isLoading, setIsLoading] = useState(true);
  if (!burger_toppings)
    burger_toppings = JSON.parse(localStorage.getItem("burger")).consists_of;

  const difference = (toppings, my_toppings) => {
    let arr = new Set();
    for (let i = 0; i < toppings.length; i++) {
      for (let j = 0; j < my_toppings.length; j++) {
        if (toppings[i].name === my_toppings[j].name) {
          arr.add(my_toppings[j]); //qty = 1
        } else {
          arr.add(toppings[i]); //qty = 0
        }
      }
    }
    console.log(arr)
    return [...arr];
  };

  useEffect(() => {
    setIsLoading(true);
    fetch(db_url)
      .then((res) => res.json())
      .then((res) => {
        let newToppings = difference(res, burger_toppings);
        setToppings(newToppings);
        setIsLoading(false);
      })
      .catch((e) => {
        setIsLoading(true);
        setError({ msg: "Виникла помилка" + e, status: true });
      });
  }, [db_url]);

  return { toppings, error, isLoading };
}
