import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import { BurgerIngridients } from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";
import { useHistory } from "react-router-dom";
import { BurgerContext } from "../../context/burgerContext";

export default function Index({ id }) {
  const [cachedBurger, setCachedBurger] = useState("");
  const { burger, setBurger } = useContext(BurgerContext);
  const history = useHistory();

  useEffect(() => {
    if(burger.name.length === 0) {
      history.push('/')
    }
  }, [burger]);

  return (
    <Wrapper>
      <Menu>
        <MenuTitle>{burger && burger.name}</MenuTitle>
        <BurgerIngridients>
          {burger.consists_of && burger.consists_of.map(({ name, quantity }) => {
            return (
              <Ingridient
                key={id}
                text={name}
                initValue={quantity}
                increase={() => {}}
                descrease={() => {}}
              />
            );
          })}
        </BurgerIngridients>
      </Menu>
    </Wrapper>
  );
}
