import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import {
  BurgerIngridients,
  IngridientWrapper,
  BurgerDetails,
  DetailsTitle,
  IngridientsDetails,
} from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";

import { BurgerContext } from "../../context/burgerContext";
import useToppings from "../../hooks/useToppings";
import IngridientdetailBlock from "../../component/IngridientsDetailBlock";

export default function Index() {
  const { burger, setBurger } = useContext(BurgerContext);

  const { toppings, error, isLoading } = useToppings(
    "http://localhost:3000/toppings",
    burger ? burger.consists_of : []
  );


  useEffect(() => {
    setBurger(JSON.parse(localStorage.getItem("burger")));
  }, []);

  const [number, setNumber] = useState(0);
  const add = (text, number) => {
    for(let i = 0; i < burger.consists_of.length; i++) {
      if(burger.consists_of[i].name === text) {
        burger.consists_of[i].quantity = number;
      }
    }
    console.log(burger)
    setNumber(p => p + 1)
  }

  // useEffect(() => {
  //   setBurger(JSON.parse(localStorage.getItem("burger")));
  // }, [number]);

  if (!isLoading)
    return (
      <Wrapper>
        <Menu>
          <MenuTitle>{burger && burger.name}</MenuTitle>
          <IngridientWrapper>
            <BurgerIngridients>
              {toppings &&
                toppings.map(({ name, quantity, id }) => {
                  return (
                    <Ingridient key={id} text={name} add={add}  initValue={quantity} />
                  );
                })}
            </BurgerIngridients>
            <BurgerDetails>
              <DetailsTitle>{burger && burger.name} | інформація</DetailsTitle>
              <IngridientsDetails>
                {toppings.map((el) => {
                  return (
                    <IngridientdetailBlock key={el.id} text={el.name} price={el.quantity * el.cost}></IngridientdetailBlock>
                  );
                })}
              </IngridientsDetails>
            </BurgerDetails>
          </IngridientWrapper>
        </Menu>
      </Wrapper>
    );

    if (isLoading) {
      return (
        <Wrapper>
          <Menu>Loading...</Menu>
        </Wrapper>
      );
    }
}
