import React, { useEffect, useState, useContext } from "react";

import { Wrapper } from "../../styled/general";
import { Menu, MenuTitle } from "../home/homestyled";

import { BurgerIngridients } from "./burgerredostyled";
import Ingridient from "../../component/Ingridient";
import { useHistory } from "react-router-dom";
import { BurgerContext } from "../../context/burgerContext";
import useToppings from "../../hooks/toppings";

export default function Index({ id }) {
  const [cachedBurger, setCachedBurger] = useState("");
  const { burger, setBurger } = useContext(BurgerContext);
  const { toppings, error, isLoading } = useToppings(
    "http://localhost:3000/toppings",
    burger.consists_of
  );
  const history = useHistory();

  if (isLoading) {
    return (
      <Wrapper>
        <Menu>
          <MenuTitle>{burger && burger.name}</MenuTitle>
          <BurgerIngridients>loading...</BurgerIngridients>
        </Menu>
      </Wrapper>
    );
  }

  if (!isLoading)
    return (
      <Wrapper>
        <Menu>
          <MenuTitle>{burger && burger.name}</MenuTitle>
          <BurgerIngridients>
            {toppings &&
              toppings.map(({ name, quantity }) => {
                return <Ingridient key={id} text={name} initValue={quantity} />;
              })}
          </BurgerIngridients>
        </Menu>
      </Wrapper>
    );
}
